	(31, 1, '2016-01-16 15:53:55', '2016-01-16 15:53:55', '<div ng-app="horseApp">\r\n<div ng-controller="MainCtrl">\r\n<div data-ng-repeat="horse in horses">\r\n<!-- record that this customer has been selected -->\r\n<input type="checkbox" ng-model="horse.selectedHorse" />\r\n{{ horse.horseid }} {{ horse.horsename }} {{ horse.value }} {{horse.selectedHorse}}\r\n<br>\r\n\r\n</div>\r\n<input type="submit" name="submit" value="submit" ng-click="post(horses)"/>\r\n<br>\r\n\r\n<br>\r\n\r\n<br>\r\n<table>\r\n<!-- table heading goes here -->\r\n<tbody>\r\n<!-- use our custom filter to only display customers from the cities selected -->\r\n<tr data-ng-repeat="horse in horses | selectedHorses">\r\n<td>{{ horse.horseid }}</td>\r\n<td>{{ horse.horsename }}</td>\r\n<td>{{selectedHorses}}</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<br>\r\n\r\n</div>\r\n</div>', 'HorseSelector', '', 'publish', 'closed', 'closed', '', 'horseselector', '', '', '2016-01-16 15:53:55', '2016-01-16 15:53:55', '', 0, 'http://localhost/fss_frontendv2/?page_id=31', 0, 'page', '', 0),
